import React from "react";
import styled, { keyframes } from "styled-components";
import Switch from "react-switch";

class About extends React.Component {
  state = { long: true };

  handleChange = () => {
    this.setState(state => ({ long: !state.long }));
  };

  render() {
    return (
      <AboutContainer>
        <h1>About</h1>
        <Choice>
          <Switch
            onChange={this.handleChange}
            checked={this.state.long}
            uncheckedIcon={false}
            checkedIcon={false}
            onColor="#222f3e"
            offColor="#ccc"
          />
          <p>{this.state.long ? "Long Version" : "Short Version"}</p>
        </Choice>

        <Bio>
          Hi, my name is Daniel and I am a software engineer with a background
          and education in finance. I am originally from Birmingham, Alabama,
          but recently relocated to Salt Lake City, Utah to pursue my interest
          in software engineering. After working in the banking industry for a
          few years after college, I decided I wanted to pursue an exciting and
          challenging career in software development. I am honored to have
          received the Opportunity in Tech Scholarship for Dev Point Labs,
          located in the heart of Salt Lake City, UT. I am currently a student
          at Dev Point Labs and will graduate from the University of Utah
          partnered program in February of 2019.
        </Bio>
        <Bio>
          I have a passion for design as well as an even greater hunger for
          development. I love helping others learn how to code and see them get
          excited about things they are building. I love to be challenged every
          day whether it be a new algorithm challenge or designing and
          developing an application to solve a unique problem with technology
          I've never used before. I thrive in fast paced environments,
          surrounded by people with similair interests. In my free time you can usually find me snowboarding with friends, playing video games, traveling all over the world, trying a new restaurant, or exploring the outdoors with my amazing wife Jess and my goofy golden retriver named Riggs. 
        </Bio>

        <Bio>A few of the things I am skilled in include:</Bio>
      </AboutContainer>
    );
  }
}

const fadeIn = keyframes`
    0% {
        opacity: 0;
    }
    100% {
        opacity: 1.0;
    }
`;

const Choice = styled.span`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  align-self: flex-end;
  z-index: 1;

  p {
    margin-left: 10px;
  }
`;

const AboutContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  height: 100%;
  min-height: 100vh;
  width: 100%;
  padding: 2em;
  animation: ${fadeIn} 1s linear;
  max-width: 800px;
  margin: 100px auto 0;

  h1 {
    &:before {
      display: block;
      content: "";
      width: 50px;
      height: 10px;
      background-color: #222f3e;
    }
  }
`;

const Bio = styled.p`
  line-height: 1.5;
  font-size: 18px;
  padding-top: 25px;

  img {
    float: right;
    @media (max-width: 425px) {
      width: 100%;
    }
  }
`;

export default About;
