import React from "react";
import styled, { keyframes } from "styled-components";

class About extends React.Component {
  state = { long: true };

  handleChange = () => {
    this.setState(state => ({ long: !state.long }));
  };

  render() {
    return (
      <AboutContainer>
        <h1>About</h1>
        <Choice>
        </Choice>

        <Bio>
          Hi, my name is Daniel and I am a software engineer with a background
          and education in finance. I am originally from Birmingham, Alabama,
          but recently relocated to Salt Lake City, Utah to pursue my interest
          in software engineering. After working in the banking industry for a
          few years after college, I decided to pursue an exciting and
          challenging career in software development. I was honored to receive the Opportunity in Tech Scholarship for Dev Point Labs,
          located in the heart of Salt Lake City, UT. I am currently a student
          at Dev Point Labs and will graduate from the University of Utah
          partnered program in February of 2019.
        </Bio>
        <Bio>
          I have a passion for design as well as an even greater hunger for
          development. I love helping others learn how to code and see them get
          excited about things they are building. I love to be challenged every
          day whether it be a new algorithm challenge or designing and
          developing an application to solve a unique problem with technology
          I've never used before. I thrive in fast paced environments,
          surrounded by people with similair interests. In my free time you can usually find me snowboarding with friends, playing video games, traveling all over the world, trying a new restaurant, or exploring the outdoors with my amazing wife Jess and my goofy golden retriever named Riggs. 
        </Bio>

        <Bio>A few of the things I am skilled with include:</Bio>
        <SkillsList>
            <Skill>
            <svg viewBox="0 0 128 128">
                <path fill="#000" d="M2 1v125h125v-125h-125zm66.119 106.513c-1.845 3.749-5.367 6.212-9.448 7.401-6.271 1.44-12.269.619-16.731-2.059-2.986-1.832-5.318-4.652-6.901-7.901l9.52-5.83c.083.035.333.487.667 1.071 1.214 2.034 2.261 3.474 4.319 4.485 2.022.69 6.461 1.131 8.175-2.427 1.047-1.81.714-7.628.714-14.065-.001-10.115.046-20.188.046-30.188h11.709c0 11 .06 21.418 0 32.152.025 6.58.596 12.446-2.07 17.361zm48.574-3.308c-4.07 13.922-26.762 14.374-35.83 5.176-1.916-2.165-3.117-3.296-4.26-5.795 4.819-2.772 4.819-2.772 9.508-5.485 2.547 3.915 4.902 6.068 9.139 6.949 5.748.702 11.531-1.273 10.234-7.378-1.333-4.986-11.77-6.199-18.873-11.531-7.211-4.843-8.901-16.611-2.975-23.335 1.975-2.487 5.343-4.343 8.877-5.235l3.688-.477c7.081-.143 11.507 1.727 14.756 5.355.904.916 1.642 1.904 3.022 4.045-3.772 2.404-3.76 2.381-9.163 5.879-1.154-2.486-3.069-4.046-5.093-4.724-3.142-.952-7.104.083-7.926 3.403-.285 1.023-.226 1.975.227 3.665 1.273 2.903 5.545 4.165 9.377 5.926 11.031 4.474 14.756 9.271 15.672 14.981.882 4.916-.213 8.105-.38 8.581z"></path>
            </svg>
            </Skill>
            <Skill>React/Redux</Skill>
            <Skill>HTML/CSS/SASS</Skill>
            <Skill>Ruby on Rails</Skill>
            <Skill>Node/Express</Skill>
            <Skill>MySQL, PostgreSQL</Skill>
        </SkillsList>
      </AboutContainer>
    );
  }
}

const SkillsList = styled.ul`
    list-style: none;
    margin-top: 25px;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
`;

const Skill = styled.li`
    svg {
        width: 100%;
    }
`;

const fadeIn = keyframes`
    0% {
        opacity: 0;
    }
    100% {
        opacity: 1.0;
    }
`;

const Choice = styled.span`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  align-self: flex-end;
  z-index: 1;

  p {
    margin-left: 10px;
  }
`;

const AboutContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  height: 100%;
  min-height: 100vh;
  width: 100%;
  padding: 2em;
  animation: ${fadeIn} 1s linear;
  margin-top: 100px;

  h1 {
    &:before {
      display: block;
      content: "";
      width: 50px;
      height: 10px;
      background-color: #222f3e;
    }
  }
`;

const Bio = styled.p`
  line-height: 1.5;
  font-size: 18px;
  padding-top: 25px;

  img {
    float: right;
    @media (max-width: 425px) {
      width: 100%;
    }
  }
`;

export default About;
